        direction="column"
        wrap="nowrap"
        {...restProps}
      >
        {/* Fix Safari sticky header https://bugs.webkit.org/show_bug.cgi?id=175029 */}
        <div>
          <Grid
            ref={this.layoutHeader}
            item
            className={classes.stickyHeader}
          >
            <DayScale />
          </Grid>
          <Grid
            item
