import * as React from 'react';
import { toUTCDateTime } from '../utils';
import { DAYS_IN_WEEK_COUNT } from '../constants';
import { ZonedDate, MS_PER_DAY } from '@progress/kendo-date-math';
/** @hidden */

export var generateWeekSlots = function (dateRange, timezone) {
  var localStart = dateRange.start;
  var localEnd = dateRange.end;
  var startDay = ZonedDate.fromUTCDate(toUTCDateTime(localStart), timezone);
  var startTime = startDay.getTime();
  var endDay = ZonedDate.fromUTCDate(toUTCDateTime(localEnd), timezone);
  var endTime = endDay.getTime();
  var slots = [];
  var base = startTime;
  var step = MS_PER_DAY * DAYS_IN_WEEK_COUNT;
  var total = endTime - startTime;
  var index = 0;
  var distance = 0;

  while (distance < total) {
    var ref = React.createRef();
    var start = new Date(base + index * step);
    var zonedStart = ZonedDate.fromLocalDate(start, timezone);
    var end = new Date(base + (index + 1) * step);
    var zonedEnd = ZonedDate.fromLocalDate(end, timezone);
    var slot = {
      ref: ref,
      end: end,
      index: index,
      start: start,
      zonedStart: zonedStart,
      zonedEnd: zonedEnd,
      isAllDay: true,
      workDay: false,
      workHour: false,
      isMajor: false,
      isEdge: false,
      items: [],
      rangeIndex: 0
    };
    index++;
    distance += step;
    slots.push(slot);
  }

  return slots;
};
